def nombre_valeurs (array)
	puts "Il y a #{array.length} journalistes dans la table"
	return array.length
end

def Nb_Number_Array (array)
	res = 0
	array.length.times do |i|
#  "str =~ /\d/" fonction regex , renvoy vrai si str contient un entier
		if (array[i] =~ /\d/) then
			res +=1 
		end
	end
	puts "Il y a #{res} handles avec chiffres"
	return res 	
end

def Nb_Mot_Array (array, mot)
	res = 0
	array.length.times do |i|
		if (Contain_Chars_succ(array[i],mot)) then
			#puts array[i].downcase
			res +=1 
		end
	end
	puts "Il y a #{res} handles contenant #{mot}"
	return res 	
end

#downcase met le text à la minuscule
def  Contain_Chars_succ(str,mot)
	index_mot =0
	str.length.times do |i|
		if( str[i].downcase.eql?(mot[index_mot].downcase) )
			index_mot+=1
		end
		if( index_mot > mot.length-1)
			return true 
		end
	end
	return false
end

#Verifie si Str contient une lettre majuscule
def Contain_Uppercase_Char str
	if /[[:upper:]]/.match(str) then return true else return false end 
end

def Nb_Str_contain_upper(array)
	res = 0
	array.length.times do |i|
		if (Contain_Uppercase_Char array[i]) then
			res +=1 
		end
	end
	puts "Il y a #{res} handles contenant au moin une lette en Majuscule"
	return res 
end

def Nb_Str_Begin_upper(array)
	res = 0
	array.length.times do |i|
		if ( Contain_Uppercase_Char array[i][1]) then
			res +=1 
		end
	end
	puts "Il y a #{res} handles commencant par une Majuscule"
	return res 
end

def Nb_underscore str
	res = 0
	str.length.times do |i|
		if ( str[i] == '_') then
			res +=1 
		end
	end
	return res 
end

def Nb_underscore_array array
	res = 0
	array.length.times do |i|
		res+= Nb_underscore array[i]
	end
	puts "Il y a #{res} underscore au total"
	return res 
end

def Pos_Handle(array,handle)
	array.length.times do |i|
		if( array[i].eql?(handle)) then
			puts "Le hande : #{handle} se trouve à la position #{i} du tableau"
			return i
		end
	end
	return -1
end

def trie_alphabetic array
	print array.sort
end

def shortest_handles (array , nb)
	tmp = array.sort_by(&:length)
	puts "Les #{nb} handles les plus courts :"
	for i in 0..nb-1
		puts"#{tmp[i]} "
	end
	puts ""
end

